mapper.select:
SELECT * FROM user
where
id != 0

  AND full_name like CONCAT(#{fullName}, '%')


  AND phone_number like CONCAT(#{phoneNumber}, '%')


  AND email like CONCAT(#{email}, '%')


  AND address like CONCAT('%',CONCAT(#{address}, '%'))


  AND id = #{id}


  limit #{limit}

   offset #{offset}
